This example shows how to use a DeflateStream to compress data from
a file, and store the compressed data into another file.


    using (var output = System.IO.File.Create(fileToCompress + ".deflated"))
    {
    using (System.IO.Stream input = System.IO.File.OpenRead(fileToCompress))
    {
    using (Stream compressor = new DeflateStream(output, CompressionMode.Compress,
    CompressionLevel.BestCompression, true))
    {
    byte[] buffer = new byte[WORKING_BUFFER_SIZE];
    int n= -1;
    while (n != 0)
    {
    if (n > 0)
    compressor.Write(buffer, 0, n);
    n= input.Read(buffer, 0, buffer.Length);
    }
    }
    }
    // can write additional data to the output stream here
    }



    Using output As FileStream = File.Create(fileToCompress & ".deflated")
    Using input As Stream = File.OpenRead(fileToCompress)
    Using compressor As Stream = New DeflateStream(output, CompressionMode.Compress,
    CompressionLevel.BestCompression, True)
    Dim buffer As Byte() = New Byte(4096) {}
    Dim n As Integer = -1
    Do While (n <> 0)
    If (n > 0) Then
    compressor.Write(buffer, 0, n)
    End If
    n = input.Read(buffer, 0, buffer.Length)
    Loop
    End Using
    End Using
    ' can write additional data to the output stream here.
    End Using

